import React from 'react';
import { NavLink } from 'react-router-dom';
//voici une navigation avec condition et props car suivant l'endoit de la navigation on peut aller dans une direction ou pas
const ButtonsScroll = (props) => {
    return (

        <div className="scroll-bottom">
            <div className="sb-main">
                {props.left &&
                    (<NavLink to={props.left} className="left hover">
                        <span> &#10092;</span>
                    </NavLink>)}

                <p className="center">scroll</p>

                {props.right
                    && (<NavLink to={props.right} className="right hover">
                        <span> &#10093;</span>
                    </NavLink>)}

            </div>
        </div>
    );
};

export default ButtonsScroll;
